jQuery(document).on("stickyTable",function(){$(".sticky-headers").scroll(function(){$(this).find("table tr.sticky-row th").css("top",$(this).scrollTop()),$(this).find("table tr.sticky-row td").css("top",$(this).scrollTop())}),$(".sticky-ltr-cells").scroll(function(){$(this).find("table th.sticky-cell").css("left",$(this).scrollLeft()),$(this).find("table td.sticky-cell").css("left",$(this).scrollLeft())}),$(".sticky-rtl-cells").scroll(function(){var t=$(this).find("table").prop("clientWidth")-$(this).prop("clientWidth");$(this).find("table th.sticky-cell").css("right",t-$(this).scrollLeft()),$(this).find("table td.sticky-cell").css("right",t-$(this).scrollLeft())})}),$(document).ready(function(){$(document).trigger("stickyTable")});
